document.addEventListener("astro:page-load",()=>{const e=document.querySelector("[data-current-project]");e?.querySelector("button")?.addEventListener("click",()=>{e.classList.add("show")},{once:!0})});const s=[...document.querySelectorAll("article[data-project] img")],o=new IntersectionObserver(e=>{e.forEach(t=>{const{target:c}=t,r=c.closest("article");t.isIntersecting?r?.classList.add("intersecting"):r?.classList.remove("intersecting")})},{threshold:1});s.forEach(e=>{o.observe(e)});
